package com.learning.comparatordemo;

class HDTV {

	int size;
	String brand;
	
	public HDTV() {
		super();
		// TODO Auto-generated constructor stub
	}

	public HDTV(int size, String brand) {
		super();
		this.size = size;
		this.brand = brand;
	}

	public int getSize() {
		return size;
	}

	public void setSize(int size) {
		this.size = size;
	}

	public String getBrand() {
		return brand;
	}

	public void setBrand(String brand) {
		this.brand = brand;
	}
	
	
}
